import React, { useState } from 'react';
import styles from './App.module.css';
import Button from 'react-bootstrap/Button';
import NavBar from './components/NavBar/NavBar';
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom';
import HomeScreen from './screens/HomeScreen/HomeScreen';
import ContactScreen from './screens/ContactScreen/ContactScreen';
import CompaniesScreen from './screens/CompaniesScreen/CompaniesScreen';
import LoginScreen from './screens/LoginScreen/LoginScreen';
import CompaniesDetailScreen from './screens/CompaniesDetailScreen/CompaniesDetailScreen';
import { Company } from './types';
import { SiteContext } from './context';
import { Form } from 'react-bootstrap/lib/Navbar';


function App() {
  const [userType, setUserType] = useState("beginner");
  const [userSessionHistoryCompanyList, setSessionHistory] = useState<Company[]>([]);
  const [companies, setCompanies] = useState<Company[]>([ ]);



  return (
    <SiteContext.Provider value={{companies:companies, userType: userType, setUserType: setUserType }}>
      <div className={`${styles.App} ${userType == 'expert' ? styles.expert : styles.beginner}`}>
        <BrowserRouter>

        <NavBar />
            <Switch>
              <Route path="/home" exact >
                <HomeScreen  />
              </Route>
              <Route path="/contact" exact>
                <ContactScreen />
              </Route>
              <Route path="/companies" exact>
                <CompaniesScreen HistoryCompanyList={userSessionHistoryCompanyList} />
              </Route>
              <Route path="/companies/:id" exact>
                <CompaniesDetailScreen />
              </Route>
              <Route path="/Login" exact>
                <LoginScreen />
              </Route>
              <Route exact path="/">
                <Redirect to="/home" />
            </Route>
            </Switch>

        </BrowserRouter>

      </div>
    </SiteContext.Provider>
  );
}

export default App;
