import { useContext } from "react";
import { Card, Button } from "react-bootstrap";
import { useHistory, useParams } from "react-router-dom";
import { SiteContext } from "../../context";

interface CompaniesDetailScreenProps {
}

interface ParamTypes {
    id: string;
}


const CompaniesDetailScreen = ({ }: CompaniesDetailScreenProps) => {
    let { id } = useParams<ParamTypes>();
    const { companies: companies } = useContext(SiteContext);
    const history = useHistory();

    const selectedCompany = companies.find(game => game.id === parseInt(id));

    if (!selectedCompany) {
        return <div>Not found!</div>;
    }

    return (
        <div className="col d-flex justify-content-center">

        <Card style={{ width: '60%' }}>
            <Card.Body>
                <Card.Title>{selectedCompany.name}</Card.Title>
                <Card.Text>
                    {selectedCompany?.name}
                </Card.Text>
                <Button variant="primary" onClick={() => {history.goBack();}}>Go back</Button>
            </Card.Body>
        </Card>
        </div>
    )
}

export default CompaniesDetailScreen;
