import React, { ChangeEventHandler, MouseEventHandler, useState } from 'react';
import styles from './App.module.css';
import Button from 'react-bootstrap/Button';
import NavBar from './components/NavBar/NavBar';
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom';
import HomeScreen from './screens/HomeScreen/HomeScreen';
import ContactScreen from './screens/ContactScreen/ContactScreen';
import HistoryScreen from './screens/HistoryScreen/HistoryScreen';
import LoginScreen from './screens/LoginScreen/LoginScreen';
import { ICompanyData, HistoryResponse } from './types';
import { SiteContext } from './context';
import { Form } from 'react-bootstrap/lib/Navbar';
import Register from './screens/Register/Register';
import { Container } from 'react-bootstrap';
import auth from "./class/Auth/Auth";


function App() {
  const [userType, setUserType] = useState("beginner");
  const [userSessionHistoryCompanyList, setSessionHistory] = useState<ICompanyData[]>([]);
  const [companies, setCompanies] = useState<ICompanyData[]>([ ]);
  const [companyVAT, SetInputVat] = useState<string[]>([]);
  const [vatArray, setVatArray] = useState<string[]>([]);
  const [companyInfo, setCompanyInfo] = useState<ICompanyData[]>([]);

  
  const handleVatFromCompareForm: ChangeEventHandler<HTMLInputElement> = (event) => {
    setVatArray([...vatArray, event.target.value]);
  }
  const submitButtonCompareForm: MouseEventHandler<HTMLButtonElement> = (event)=>{
    event.preventDefault();
    SetInputVat(vatArray);
  }
  
let vat = '123' 




const AuthUserHistory = () => {

  const check:boolean = auth.Auth();

const [historyCompanyArray, setCompanyArray] = useState<ICompanyData[] | void>();;
const [loading, setLoading] = useState<boolean>(false);
const [request, setRequest] =  useState<boolean>(false);
const [lastrequest, setlastRequest] =  useState<number>();

const getCompanyDetails = async () => {
  setLoading(true);
  let options = {
    method: "GET",
    headers: {
      Accept: "application/json",
    },
  };
  let ResponseJSON :HistoryResponse = await fetch(
    `https://localhost:44385/api/history/history/all`,
    options
  )
    .then((response) => response.json())
    .then((json) => {
      setLoading(false);
      setRequest(true);
      return json;
    })
    .catch((error) => console.log(error));
   let setHistoryResponse = (ResponseJSON);

   let historyCompanyArray = setHistoryResponse.sendObj.history.length;
 }}



  return (
    <SiteContext.Provider value={{companies:companies, userType: userType, setUserType: setUserType }}>
      <div className={`${styles.App} ${userType == 'expert' ? styles.expert : styles.beginner}`}>
        <BrowserRouter>

        <NavBar />
        <Container>
            <Switch>
              <Route path="/home" exact >
                <HomeScreen handleVat = {handleVatFromCompareForm} submit = {submitButtonCompareForm} companyVAT = {companyVAT} companyInfo = {companyInfo} setCompanyInfo = {setCompanyInfo} />
              </Route>
              <Route path="/contact" exact>
                <ContactScreen />
              </Route>
              <Route path="/UserHistory" exact>
                <HistoryScreen/>
              </Route>
              <Route path="/Login" exact>
                <LoginScreen />
              </Route>
              <Route path="/register" exact>
                <Register />
              </Route>
              <Route exact path="/">
                <Redirect to="/home" />
            </Route>
            </Switch>
          </Container>
        </BrowserRouter>

      </div>
    </SiteContext.Provider>
  );
}
