import cookie from "../Cookie/Cookie";
import utility from "../Utility/Utility";

class Auth{

    /**
     * 
     * @returns bool 
     * this function will return true if the user is authenticated else false
     */

    public Auth():boolean{
        
        
        return utility.AuthSession("auth");
    }


    public destroy():void{
        cookie.DeleteCookie("auth");
    }

  

    
}

const auth = new Auth();
export  default auth;
